;(function ($, _, undefined) {
    'use strict';
    ips.createModule('ips.ui.storm.aes', () => {
        /**
         * Respond to a dialog trigger
         *
         * @param   {element}   elem        The element this widget is being created on
         * @param   {object}    options     The options passed
         * @param   {event}     e           if lazyload, event that is fire
         * @returns {void}
         */
        const respond = (elem, options, e) => {
            let el = $(elem);
            if (!el.data('_loadedDevToysaes')) {
                let mobject = _objectDevToysaes(el, options);
                mobject.init();
                el.data('_loadedDevToysaes', mobject);
            }
        }, /**
         * Retrieve the instance (if any) on the given element
         *
         * @param  {element}  elem    The element to check
         * @returns {mixed}  The instance or undefined
         */
        getObj = (elem) => {
            if ($(elem).data('_loadedDevToysBase')) {
                return $(elem).data('_loadedDevToysBase');
            }
            return undefined;
        };

        // Register this module as a widget to enable the data API and
        // jQuery plugin functionality
        ips.ui.registerWidget('stormdevtoysaes', ips.ui.storm.aes, []);

        // Expose public methods
        return {
            respond: respond, getObj: getObj,
        };
    });
    const _objectDevToysaes = function (elem, options) {
        var ajax = ips.getAjax(),
            init = () => {
                elem.on('click', '[data-decode]', _decode);
                elem.on('click', '[data-encode]', _encode);
                elem.on('click', '[data-clear]', _clear);
                elem.on('click', '[data-copy]', _copy);
                ips.storm.copy.handlePermissions();
            }, 
            _clear = e => {
                let target = $(e.currentTarget), 
                    ec = target.attr('data-clear');
                if (ec === 'decode') {
                    elem.find('#elDecodedKey').val('');
                    elem.find('#elDecodedBits').val(128);
                    elem.find('#elDecodedAesContainer').val('');
                } else {
                    elem.find('#elEncodeddKey').val('');
                    elem.find('#elEncodedBits').val(128);
                    elem.find('#elEncodedAesContainer').val('');
                }
            }, 
            _copy = e => {
                let message = 'AES Copied to clipboard!';
                ips.storm.copy.copy(e, message, true);
            }, 
            _encode = () => {
                let data = {
                    key: elem.find('#elDecodedKey').val(),
                    bits: elem.find('#elDecodedBits').val(),
                    content: elem.find('#elDecodedAesContainer').val(),
                    ec: 'encode',
                };
                _lookup(data);
            }, 
            _decode = () => {
                let data = {
                    key: elem.find('#elEncodeddKey').val(),
                    bits: elem.find('#elEncodedBits').val(),
                    content: elem.find('#elEncodedAesContainer').val(),
                    ec: 'decode',
                };
                _lookup(data);
            }, 
            _lookup = (data) => {
                let url = ips.getSetting('baseURL') +
                    'index.php?app=storm&module=other&controller=toys&do=aes';
                ajax({
                    type: 'POST',
                    url: url,
                    data: data,
                    dataType: 'json',
                    bypassRedirect: true,
                    success: function (data) {
                        if (data.hasOwnProperty('ec')) {
                            let message = '';
                            if (data.ec === 'decode') {
                                elem.find('#elDecodedKey').val(data.key);
                                elem.find('#elDecodedBits').val(data.bits);
                                elem.find('#elDecodedAesContainer').val(data.content);
                                message = 'Data decoded!';
                            } else {
                                elem.find('#elEncodeddKey').val(data.key);
                                elem.find('#elEncodedBits').val(data.bits);
                                elem.find('#elEncodedAesContainer').val(data.content);
                                message = 'Data encoded!';
                            }
                            ips.ui.flashMsg.show(message);
                        }

                    },
                });
            };

        return {
            init: init,
        };
    };
}(jQuery, _));

;(function ($, _, undefined) {
    'use strict';
    ips.createModule('ips.storm.alert', function () {
        // Functions that become public methods

        var respond = function (elem, options) {
            if (!$(elem).data("_stormProfilerProxy")) {
                let stormProfilerProxy = new _stormProfilerProxy($(elem), options);
                stormProfilerProxy.init();
                $(elem).data("_stormProfilerProxy", stormProfilerProxy);
            }
        };

        // Register this module as a widget to enable the data API and
        // jQuery plugin functionality
        ips.ui.registerWidget("stormalert", ips.storm.alert, ['type','msg', 'url']);

        // Expose public methods
        return {
            respond: respond,
        };
    });

    let _stormProfilerProxy = function (el, options) {
        var ajax = ips.getAjax(),
            init = function () {
                $(document).on('submitDialog', (e,f) => {
                    let data = f.response;
                    console.log(data);
                   if(data.success === 'storm_devcenter_other'){
                       ips.ui.flashMsg.show(
                           data.message,
                           {
                               timeout: data.timeout,
                               escape: data.escape
                           }
                       );
                   }
                })

                let config = {};

                config.type = options.type;
                config.message = options.msg;
                if(_.isUndefined(options.url) && !_.isUndefined(el.attr('href'))){
                    options.url = el.attr('href');
                }
                switch(config.type){
                    case 'confirm':
                        config.icon = 'question';
                        config.buttons = {
                            ok: ips.getString('yes'),
                            cancel: ips.getString('no'),
                        }
                        config.callbacks = {
                            ok: _okay,
                            cancel: function (e) {
                            }
                        }
                        break;
                    case 'info':
                        config.icon = 'info';
                        config.buttons = {
                            ok: ips.getString('ok'),
                        };
                        config.callbacks = {
                            ok: _okay,
                        }
                        break;
                    case 'success':
                        config.icon = 'success';
                        break;
                    case 'warn':
                        config.icon = 'warn';
                        break;
                    case 'ok':
                        config.icon = 'ok';
                        break;
                }

                el.on('click', e => {
                    e.preventDefault();
                    ips.ui.alert.show(config)
                });

            },
        _okay = e => {
            ajax({
                    type: 'GET',
                    url: options.url,
                    dataType: 'json',
                    bypassRedirect: true,
                    showLoading:true,
                    beforeSend: function() {
                    },
                    success: function(data) {
                        if(data.hasOwnProperty('message')){
                            ips.ui.flashMsg.show(data.message);
                        }
                        if(data.hasOwnProperty('redirect')){
                            window.location.href = data.redirect;
                        }
                    },
                    error: function(data) {
                    }
                });
        };
        return {
            init: init
        }
    }
}(jQuery, _));

;(function ($, _, undefined) {
    "use strict";
    ips.createModule('ips.ui.storm.base', () => {
        /**
         * Respond to a dialog trigger
         *
         * @param   {element}   elem        The element this widget is being created on
         * @param   {object}    options     The options passed
         * @param   {event}     e           if lazyload, event that is fire
         * @returns {void}
         */
        const respond = (elem, options, e) => {
                let el = $(elem);
                if (!el.data('_loadedDevToysBase')) {
                    let mobject = _objectDevToysBase(el, options);
                    mobject.init();
                    el.data('_loadedDevToysBase', mobject);
                }
            },
            /**
             * Retrieve the instance (if any) on the given element
             *
             * @param    {element}    elem        The element to check
             * @returns {mixed}    The instance or undefined
             */
            getObj = (elem) => {
                if ($(elem).data('_loadedDevToysBase')) {
                    return $(elem).data('_loadedDevToysBase');
                }
                return undefined;
            };

        // Register this module as a widget to enable the data API and
        // jQuery plugin functionality
        ips.ui.registerWidget('stormdevtoysbase', ips.ui.storm.base, []);

        // Expose public methods
        return {
            respond: respond,
            getObj: getObj
        };
    });
    const _objectDevToysBase = function (elem, options) {
        var init = () => {
                elem.on('keyup input propertychange', '#elDecodedContainer', _encode);
                elem.on('keyup input propertychange', '#elEncodedContainer', _decode);
                elem.on('click', '[data-clear]', _clear);
                elem.on('click', '[data-copy]', _copy);
                ips.storm.copy.handlePermissions();
            },
            _clear = e => {
                let target = $(e.currentTarget),
                    id = target.attr('data-clear');
                $('#' + id).val(' ');
            },
            _copy = e => {
                let target = $(e.currentTarget),
                    message = target.attr('data-target-type') + ' Copied to clipboard!';
                ips.storm.copy.copy(e, message, true);
            },
            _encode = () => {
                let decoded = elem.find('#elDecodedContainer').val();
                elem.find('#elEncodedContainer').val(btoa(decoded));
            },
            _decode = () => {
                let encoded = elem.find('#elEncodedContainer').val();
                elem.find('#elDecodedContainer').val(atob(encoded));
            };
        return {
            init: init
        }
    };
}(jQuery, _));

