        };
    });
    const _objectDevToysBitwise = function (elem, options) {
        var ajax = ips.getAjax(),
            init = () => {
                elem.on('keyup input propertychange change', '#position',
                    _position);
            },
            _position = e => {
                e.preventDefault();
                let el = elem.find('#position'),
                    value = el.val(),
                    action = ips.getSetting('baseURL') +
                        '?app=storm&module=other&controller=toys&do=bitwiseValues&position=' +
                        value;
                ajax({
                    type: 'GET',
                    url: action,
                    bypassRedirect: true,
                    success: function (data) {
                        $('#elBitWiseBox').replaceWith($(data));
                    },
                });
            };
        return {
            init: init,
        };
    };
}(jQuery, _));

;(function ($, _, undefined) {
    'use strict';
    ips.createModule('ips.storm.copy', function () {
        var
             report =  (msg) => {
            // console.log(msg);
        },
        handlePermissions = () => {
            navigator.permissions.query({name:'clipboard-write'})
                .then(function(result) {
                    if (result.state == 'granted') {
                    } else if (result.state == 'prompt') {
                        report(result.state);
                    } else if (result.state == 'denied') {
                        report(result.state);
                    }
                    result.onchange = function() {
                        report(result.state);
                    }
                });
        },
        copy = (e, message, val) => {
            let target = $(e.currentTarget),
                id = target.attr('data-copy'),
                text;
            if( val === true){
                text = $('#' + id).val();
            }
            else{
                text = $('#' + id).text();
            }
            try {
                navigator.clipboard.writeText(text);
                ips.ui.flashMsg.show(message);

            } catch (err) {
                var textArea = document.createElement('textarea');
                textArea.style.position = 'fixed';
                textArea.style.top = 0;
                textArea.style.left = 0;
                textArea.style.width = '2em';
                textArea.style.height = '2em';
                textArea.style.padding = 0;
                // Clean up any borders.
                textArea.style.border = 'none';
                textArea.style.outline = 'none';
                textArea.style.boxShadow = 'none';
                // Avoid flash of white box if rendered for any reason.
                textArea.style.background = 'transparent';
                textArea.value = text;
                document.body.appendChild(textArea);

                textArea.select();
                var successful = document.execCommand('copy');
                document.body.removeChild(textArea);
                if (successful) {
                    ips.ui.flashMsg.show(message);
                }
            }
        };
        return {
            copy:copy,
            report: report,
            handlePermissions: handlePermissions,
        };
    });
}(jQuery, _));

;( function($, _, undefined){
    "use strict";
    ips.createModule('ips.ui.storm.dates', () => {
        /**
         * Respond to a dialog trigger
         *
         * @param   {element}   elem        The element this widget is being created on
         * @param   {object}    options     The options passed
         * @param   {event}     e           if lazyload, event that is fire
         * @returns {void}
         */
         const respond = (elem, options, e) => {
            let el = $(elem);
            if (!el.data('_loadedDevToysDates')) {
                let mobject = _objectDevToysDates(el, options);
                mobject.init();
                el.data('_loadedDevToysDates', mobject);
            }
        },
        /**
         * Retrieve the instance (if any) on the given element
         *
         * @param	{element} 	elem 		The element to check
         * @returns {mixed} 	The instance or undefined
         */
        getObj = (elem) => {
            if( $( elem ).data('_loadedDevToysDates') ){
                return $( elem ).data('_loadedDevToysDates');
            }
            return undefined;
        };

        // Register this module as a widget to enable the data API and
        // jQuery plugin functionality
        ips.ui.registerWidget( 'stormdevtoysdates', ips.ui.storm.dates, [] );

        // Expose public methods
        return {
            respond: respond,
            getObj: getObj
        };
    });
    const _objectDevToysDates = function(elem, options) {
        var ajax = ips.getAjax(),
            init = () => {
                elem.on('keyup input propertychange change','[data-input]',_process);
                elem.on('click','[data-copy]',_copy);
                ips.storm.copy.handlePermissions();
            },
            _process = (e) => {
                let target = $(e.currentTarget),
                    type = target.attr('data-input'),
                    number = target.val(),
                    url = ips.getSetting('baseURL')+'index.php?app=storm&module=other&controller=toys&do=dates';
                ajax({
                    type: "POST",
                    url: url,
                    data:{type:type,time:number},
                    dataType: 'json',
                    bypassRedirect: true,
                    success: function (data) {
                      $.each(data, (key,value) => {
                        console.log(key,value);
                            elem.find('#'+key).val(value);
                        });
                    }
                });
            },
            _copy = e => {
                let target = $(e.currentTarget),
                    message = target.parents('.i-margin-top_2').find('h4').text() + ' Copied to clipboard!';
                ips.storm.copy.copy(e, message, true);
            };
      return {
        init: init
      }
    };
}(jQuery, _));

/*!

 diff v5.1.0

