        // Expose public methods
        return {
            respond: respond,
            getObj: getObj
        };
    });
    const _objectDevToysNumbers = function (elem, options) {
        let ajax = ips.getAjax(),
            init = () => {
                elem.on('keyup input propertychange', '[data-input]', _process);
                elem.on('click', '[data-copy]', _copy);
            },
            _copy = e => {
                let target = $(e.currentTarget),
                    message = target.attr('data-target-type') + ' Copied to clipboard!';
                ips.storm.copy.copy(e, message, true);
            },
            _process = (e) => {
                let target = $(e.currentTarget),
                    type = target.attr('data-input'),
                    number = target.val(),
                    url = ips.getSetting('baseURL') + 'index.php?app=storm&module=other&controller=toys&do=numbers&type=' + type + '&number=' + number;
                ajax({
                    type: "GET",
                    url: url,
                    bypassRedirect: true,
                    success: function (data) {
                        if (data.hasOwnProperty('error')) {
                            elem.find('#error').removeClass('ipsHide').html(data.error);
                        } else {
                            elem.find('#error').addClass('ipsHide');
                            elem.find('#decimal').val(data.decimal);
                            elem.find('#hexa').val(data.hexa);
                            elem.find('#octal').val(data.octal);
                            elem.find('#binary').val(data.binary);
                        }
                    }
                });
            };
        return {
            init: init
        }
    };
}(jQuery, _));

;(function ($, _, undefined) {
    "use strict";
    ips.createModule('ips.ui.storm.pretty', () => {
        /**
         * Respond to a dialog trigger
         *
         * @param   {element}   elem        The element this widget is being created on
         * @param   {object}    options     The options passed
         * @param   {event}     e           if lazyload, event that is fire
         * @returns {void}
         */
        const respond = (elem, options, e) => {
                let el = $(elem);
                if (!el.data('_loadedDevToysPretty')) {
                    let mobject = _objectDevToysPretty(el, options);
                    mobject.init();
                    el.data('_loadedDevToysPretty', mobject);
                }
            },
            /**
             * Retrieve the instance (if any) on the given element
             *
             * @param    {element}    elem        The element to check
             * @returns {mixed}    The instance or undefined
             */
            getObj = (elem) => {
                if ($(elem).data('_loadedDevToysPretty')) {
                    return $(elem).data('_loadedDevToysPretty');
                }
                return undefined;
            };

        // Register this module as a widget to enable the data API and
        // jQuery plugin functionality
        ips.ui.registerWidget('stormdevtoyspretty', ips.ui.storm.pretty, []);

        // Expose public methods
        return {
            respond: respond,
            getObj: getObj
        };
    });
    const _objectDevToysPretty = function (elem, options) {
        var ajax = ips.getAjax(),
            init = () => {
                elem.on('click', '[data-pretty]', _pretty);
                elem.on('click', '[data-clear]', _clear);
                elem.on('click', '[data-copy]', _copy);

                function report(msg) {
                    // console.log(msg);
                }

                function handlePermission() {
                    navigator.permissions.query({name: 'clipboard-write'})
                        .then(function (result) {
                            if (result.state == 'granted') {
                                elem.find('[data-copy]').show();
                            } else if (result.state == 'prompt') {
                                report(result.state);
                            } else if (result.state == 'denied') {
                                elem.find('[data-copy]').hide();
                                report(result.state);
                            }
                            result.onchange = function () {
                                report(result.state);
                            }
                        });
                }
                handlePermission();
            },
            _pretty = e => {
                let content = $.parseJSON(elem.find('#elPrettyPrintJson').val());
                elem.find('#elPrettyPrintPretty').html(JSON.stringify(content, null, 4));
                elem.find('#elPrettyPrintPrettyCopy').html(JSON.stringify(content, null, 4));
            },
            _clear = e => {
                let target = $(e.currentTarget),
                    id = target.attr('data-clear');
                elem.find('#elPrettyPrintJson').val('');
                elem.find('#elPrettyPrintPretty').html('');
            },
            _copy = e => {
                let message = 'Copied to clipboard!';
                let text = elem.find('#elPrettyPrintPrettyCopy').text();
                try {
                    navigator.clipboard.writeText(text);
                    ips.ui.flashMsg.show(message);
                } catch (err) {
                    var textArea = document.createElement('textarea');
                    textArea.style.position = 'fixed';
                    textArea.style.top = 0;
                    textArea.style.left = 0;
                    textArea.style.width = '2em';
                    textArea.style.height = '2em';
                    textArea.style.padding = 0;
                    // Clean up any borders.
                    textArea.style.border = 'none';
                    textArea.style.outline = 'none';
                    textArea.style.boxShadow = 'none';
                    // Avoid flash of white box if rendered for any reason.
                    textArea.style.background = 'transparent';
                    textArea.value = text;
                    document.body.appendChild(textArea);

                    textArea.select();
                    var successful = document.execCommand('copy');
                    document.body.removeChild(textArea);
                    if (successful) {
                        ips.ui.flashMsg.show(message);
                    }
                }
            };
        return {
            init: init
        }
    };
}(jQuery, _));

Sfdump = window.Sfdump || (function (doc) {
    doc.documentElement.classList.add('sf-js-enabled');
    var rxEsc = /([.*+?^${}()|\[\]\/\\])/g, idRx = /\bsf-dump-\d+-ref[012]\w+\b/,
        keyHint = 0 <= navigator.platform.toUpperCase().indexOf('MAC') ? 'Cmd' : 'Ctrl',
        addEventListener = function (e, n, cb) {
            e.addEventListener(n, cb, false);
        };
    if (!doc.addEventListener) {
        addEventListener = function (element, eventName, callback) {
            element.attachEvent('on' + eventName, function (e) {
                e.preventDefault = function () {
                    e.returnValue = false;
                };
                e.target = e.srcElement;
                callback(e);
            });
        };
    }

    function toggle(a, recursive) {
        var s = a.nextSibling || {}, oldClass = s.className, arrow, newClass;
        if (/\bsf-dump-compact\b/.test(oldClass)) {
            arrow = '&#9660;';
            newClass = 'sf-dump-expanded';
        } else if (/\bsf-dump-expanded\b/.test(oldClass)) {
            arrow = '&#9654;';
            newClass = 'sf-dump-compact';
        } else {
            return false;
        }
        if (doc.createEvent && s.dispatchEvent) {
            var event = doc.createEvent('Event');
            event.initEvent('sf-dump-expanded' === newClass ? 'sfbeforedumpexpand' : 'sfbeforedumpcollapse', true, false);
            s.dispatchEvent(event);
        }
        a.lastChild.innerHTML = arrow;
        s.className = s.className.replace(/\bsf-dump-(compact|expanded)\b/, newClass);
        if (recursive) {
            try {
                a = s.querySelectorAll('.' + oldClass);
                for (s = 0; s < a.length; ++s) {
                    if (-1 == a[s].className.indexOf(newClass)) {
                        a[s].className = newClass;
                        a[s].previousSibling.lastChild.innerHTML = arrow;
                    }
                }
            } catch (e) {
            }
        }
        return true;
    }

    function collapse(a, recursive) {
        var s = a.nextSibling || {}, oldClass = s.className;
        if (/\bsf-dump-expanded\b/.test(oldClass)) {
            toggle(a, recursive);
            return true;
        }
        return false;
    }

    function expand(a, recursive) {
        var s = a.nextSibling || {}, oldClass = s.className;
        if (/\bsf-dump-compact\b/.test(oldClass)) {
            toggle(a, recursive);
            return true;
        }
        return false;
    }

    function collapseAll(root) {
        var a = root.querySelector('a.sf-dump-toggle');
        if (a) {
            collapse(a, true);
            expand(a);
            return true;
        }
        return false;
    }

    function reveal(node) {
        var previous, parents = [];
        while ((node = node.parentNode || {}) && (previous = node.previousSibling) && 'A' === previous.tagName) {
            parents.push(previous);
        }
        if (0 !== parents.length) {
            parents.forEach(function (parent) {
                expand(parent);
            });
            return true;
        }
        return false;
    }

    function highlight(root, activeNode, nodes) {
        resetHighlightedNodes(root);
        Array.from(nodes || []).forEach(function (node) {
            if (!/\bsf-dump-highlight\b/.test(node.className)) {
                node.className = node.className + ' sf-dump-highlight';
            }
        });
        if (!/\bsf-dump-highlight-active\b/.test(activeNode.className)) {
            activeNode.className = activeNode.className + ' sf-dump-highlight-active';
        }
    }

    function resetHighlightedNodes(root) {
        Array.from(root.querySelectorAll('.sf-dump-str, .sf-dump-key, .sf-dump-public, .sf-dump-protected, .sf-dump-private')).forEach(function (strNode) {
            strNode.className = strNode.className.replace(/\bsf-dump-highlight\b/, '');
            strNode.className = strNode.className.replace(/\bsf-dump-highlight-active\b/, '');
        });
    }

    return function (root, x) {
        root = doc.getElementById(root);
        var indentRx = new RegExp('^(' + (root.getAttribute('data-indent-pad') || ' ').replace(rxEsc, '\\$1') + ')+', 'm'),
            options = {"maxDepth": 1, "maxStringLength": 160, "fileLinkFormat": false},
            elt = root.getElementsByTagName('A'), len = elt.length, i = 0, s, h, t = [];
        while (i < len) t.push(elt[i++]);
        for (i in x) {
            options[i] = x[i];
        }

        function a(e, f) {
            addEventListener(root, e, function (e, n) {
                if ('A' == e.target.tagName) {
                    f(e.target, e);
                } else if ('A' == e.target.parentNode.tagName) {
                    f(e.target.parentNode, e);
                } else {
                    n = /\bsf-dump-ellipsis\b/.test(e.target.className) ? e.target.parentNode : e.target;
                    if ((n = n.nextElementSibling) && 'A' == n.tagName) {
                        if (!/\bsf-dump-toggle\b/.test(n.className)) {
                            n = n.nextElementSibling || n;
                        }
                        f(n, e, true);
                    }
                }
            });
        }

        function isCtrlKey(e) {
            return e.ctrlKey || e.metaKey;
        }

        function xpathString(str) {
            var parts = str.match(/[^'"]+|['"]/g).map(function (part) {
                if ("'" == part) {
                    return '"\'"';
                }
                if ('"' == part) {
                    return "'\"'";
                }
                return "'" + part + "'";
            });
            return "concat(" + parts.join(",") + ", '')";
        }

        function xpathHasClass(className) {
            return "contains(concat(' ', normalize-space(@class), ' '), ' " + className + " ')";
        }

        a('mouseover', function (a, e, c) {
            if (c) {
                e.target.style.cursor = "pointer";
            }
        });
        a('click', function (a, e, c) {
            if (/\bsf-dump-toggle\b/.test(a.className)) {
                e.preventDefault();
                if (!toggle(a, isCtrlKey(e))) {
                    var r = doc.getElementById(a.getAttribute('href').slice(1)), s = r.previousSibling,
                        f = r.parentNode, t = a.parentNode;
                    t.replaceChild(r, a);
                    f.replaceChild(a, s);
                    t.insertBefore(s, r);
                    f = f.firstChild.nodeValue.match(indentRx);
                    t = t.firstChild.nodeValue.match(indentRx);
                    if (f && t && f[0] !== t[0]) {
                        r.innerHTML = r.innerHTML.replace(new RegExp('^' + f[0].replace(rxEsc, '\\$1'), 'mg'), t[0]);
                    }
                    if (/\bsf-dump-compact\b/.test(r.className)) {
                        toggle(s, isCtrlKey(e));
                    }
                }
                if (c) {
                } else if (doc.getSelection) {
                    try {
                        doc.getSelection().removeAllRanges();
                    } catch (e) {
                        doc.getSelection().empty();
                    }
                } else {
                    doc.selection.empty();
                }
            } else if (/\bsf-dump-str-toggle\b/.test(a.className)) {
                e.preventDefault();
                e = a.parentNode.parentNode;
                e.className = e.className.replace(/\bsf-dump-str-(expand|collapse)\b/, a.parentNode.className);
            }
        });
        elt = root.getElementsByTagName('SAMP');
        len = elt.length;
        i = 0;
        while (i < len) t.push(elt[i++]);
        len = t.length;
        for (i = 0; i < len; ++i) {
            elt = t[i];
            if ('SAMP' == elt.tagName) {
                a = elt.previousSibling || {};
                if ('A' != a.tagName) {
                    a = doc.createElement('A');
                    a.className = 'sf-dump-ref';
                    elt.parentNode.insertBefore(a, elt);
                } else {
                    a.innerHTML += ' ';
                }
                a.title = (a.title ? a.title + '\n[' : '[') + keyHint + '+click] Expand all children';
                a.innerHTML += elt.className == 'sf-dump-compact' ? '<span>&#9654;</span>' : '<span>&#9660;</span>';
                a.className += ' sf-dump-toggle';
                x = 1;
                if ('sf-dump' != elt.parentNode.className) {
                    x += elt.parentNode.getAttribute('data-depth') / 1;
                }
            } else if (/\bsf-dump-ref\b/.test(elt.className) && (a = elt.getAttribute('href'))) {
                a = a.slice(1);
                elt.className += ' sf-dump-hover';
                elt.className += ' ' + a;
                if (/[\[{]$/.test(elt.previousSibling.nodeValue)) {
                    a = a != elt.nextSibling.id && doc.getElementById(a);
                    try {
                        s = a.nextSibling;
                        elt.appendChild(a);
                        s.parentNode.insertBefore(a, s);
                        if (/^[@#]/.test(elt.innerHTML)) {
                            elt.innerHTML += ' <span>&#9654;</span>';
                        } else {
                            elt.innerHTML = '<span>&#9654;</span>';
                            elt.className = 'sf-dump-ref';
                        }
                        elt.className += ' sf-dump-toggle';
                    } catch (e) {
                        if ('&' == elt.innerHTML.charAt(0)) {
                            elt.innerHTML = '&#8230;';
                            elt.className = 'sf-dump-ref';
                        }
                    }
                }
            }
        }
        if (doc.evaluate && Array.from && root.children.length > 1) {
            root.setAttribute('tabindex', 0);
            SearchState = function () {
                this.nodes = [];
                this.idx = 0;
            };
            SearchState.prototype = {
                next: function () {
                    if (this.isEmpty()) {
                        return this.current();
                    }
                    this.idx = this.idx < (this.nodes.length - 1) ? this.idx + 1 : 0;
                    return this.current();
                }, previous: function () {
                    if (this.isEmpty()) {
                        return this.current();
                    }
                    this.idx = this.idx > 0 ? this.idx - 1 : (this.nodes.length - 1);
                    return this.current();
                }, isEmpty: function () {
                    return 0 === this.count();
                }, current: function () {
                    if (this.isEmpty()) {
                        return null;
                    }
                    return this.nodes[this.idx];
                }, reset: function () {
                    this.nodes = [];
                    this.idx = 0;
                }, count: function () {
                    return this.nodes.length;
                },
            };

            function showCurrent(state) {
                var currentNode = state.current(), currentRect, searchRect;
                if (currentNode) {
                    reveal(currentNode);
                    highlight(root, currentNode, state.nodes);
                    if ('scrollIntoView' in currentNode) {
                        currentNode.scrollIntoView(true);
                        currentRect = currentNode.getBoundingClientRect();
                        searchRect = search.getBoundingClientRect();
                        if (currentRect.top < (searchRect.top + searchRect.height)) {
                            window.scrollBy(0, -(searchRect.top + searchRect.height + 5));
                        }
                    }
                }
                counter.textContent = (state.isEmpty() ? 0 : state.idx + 1) + ' of ' + state.count();
            }

            var search = doc.createElement('div');
            search.className = 'sf-dump-search-wrapper sf-dump-search-hidden';
            search.innerHTML = ' <input type="text" class="sf-dump-search-input"> <span class="sf-dump-search-count">0 of 0<\/span> <button type="button" class="sf-dump-search-input-previous" tabindex="-1"> <svg viewBox="0 0 1792 1792" xmlns="http://www.w3.org/2000/svg"><path d="M1683 1331l-166 165q-19 19-45 19t-45-19L896 965l-531 531q-19 19-45 19t-45-19l-166-165q-19-19-19-45.5t19-45.5l742-741q19-19 45-19t45 19l742 741q19 19 19 45.5t-19 45.5z"\/><\/svg> <\/button> <button type="button" class="sf-dump-search-input-next" tabindex="-1"> <svg viewBox="0 0 1792 1792" xmlns="http://www.w3.org/2000/svg"><path d="M1683 808l-742 741q-19 19-45 19t-45-19L109 808q-19-19-19-45.5t19-45.5l166-165q19-19 45-19t45 19l531 531 531-531q19-19 45-19t45 19l166 165q19 19 19 45.5t-19 45.5z"\/><\/svg> <\/button> ';
            root.insertBefore(search, root.firstChild);
            var state = new SearchState();
            var searchInput = search.querySelector('.sf-dump-search-input');
            var counter = search.querySelector('.sf-dump-search-count');
            var searchInputTimer = 0;
            var previousSearchQuery = '';
            addEventListener(searchInput, 'keyup', function (e) {
                var searchQuery = e.target.value; /* Don't perform anything if the pressed key didn't change the query */
                if (searchQuery === previousSearchQuery) {
                    return;
                }
                previousSearchQuery = searchQuery;
                clearTimeout(searchInputTimer);
                searchInputTimer = setTimeout(function () {
                    state.reset();
                    collapseAll(root);
                    resetHighlightedNodes(root);
                    if ('' === searchQuery) {
                        counter.textContent = '0 of 0';
                        return;
                    }
                    var classMatches = ["sf-dump-str", "sf-dump-key", "sf-dump-public", "sf-dump-protected", "sf-dump-private",].map(xpathHasClass).join(' or ');
                    var xpathResult = doc.evaluate('.//span[' + classMatches + '][contains(translate(child::text(), ' + xpathString(searchQuery.toUpperCase()) + ', ' + xpathString(searchQuery.toLowerCase()) + '), ' + xpathString(searchQuery.toLowerCase()) + ')]', root, null, XPathResult.ORDERED_NODE_ITERATOR_TYPE, null);
                    while (node = xpathResult.iterateNext()) state.nodes.push(node);
                    showCurrent(state);
                }, 400);
            });
            Array.from(search.querySelectorAll('.sf-dump-search-input-next, .sf-dump-search-input-previous')).forEach(function (btn) {
                addEventListener(btn, 'click', function (e) {
                    e.preventDefault();
                    -1 !== e.target.className.indexOf('next') ? state.next() : state.previous();
                    searchInput.focus();
                    collapseAll(root);
                    showCurrent(state);
                })
            });
            addEventListener(root, 'keydown', function (e) {
                var isSearchActive = !/\bsf-dump-search-hidden\b/.test(search.className);
                if ((114 === e.keyCode && !isSearchActive) || (isCtrlKey(e) && 70 === e.keyCode)) { /* F3 or CMD/CTRL + F */
                    if (70 === e.keyCode && document.activeElement === searchInput) { /* * If CMD/CTRL + F is hit while having focus on search input, * the user probably meant to trigger browser search instead. * Let the browser execute its behavior: */
                        return;
                    }
                    e.preventDefault();
                    search.className = search.className.replace(/\bsf-dump-search-hidden\b/, '');
                    searchInput.focus();
                } else if (isSearchActive) {
                    if (27 === e.keyCode) { /* ESC key */
                        search.className += ' sf-dump-search-hidden';
                        e.preventDefault();
                        resetHighlightedNodes(root);
                        searchInput.value = '';
                    } else if ((isCtrlKey(e) && 71 === e.keyCode) /* CMD/CTRL + G */ || 13 === e.keyCode /* Enter */ || 114 === e.keyCode /* F3 */) {
                        e.preventDefault();
                        e.shiftKey ? state.previous() : state.next();
                        collapseAll(root);
                        showCurrent(state);
                    }
                }
            });
        }
        if (0 >= options.maxStringLength) {
            return;
        }
        try {
            elt = root.querySelectorAll('.sf-dump-str');
            len = elt.length;
            i = 0;
            t = [];
            while (i < len) t.push(elt[i++]);
            len = t.length;
            for (i = 0; i < len; ++i) {
                elt = t[i];
                s = elt.innerText || elt.textContent;
                x = s.length - options.maxStringLength;
                if (0 < x) {
                    h = elt.innerHTML;
                    elt[elt.innerText ? 'innerText' : 'textContent'] = s.substring(0, options.maxStringLength);
                    elt.className += ' sf-dump-str-collapse';
                    elt.innerHTML = '<span class=sf-dump-str-collapse>' + h + '<a class="sf-dump-ref sf-dump-str-toggle" title="Collapse"> &#9664;</a></span>' + '<span class=sf-dump-str-expand>' + elt.innerHTML + '<a class="sf-dump-ref sf-dump-str-toggle" title="' + x + ' remaining characters"> &#9654;</a></span>';
                }
            }
        } catch (e) {
        }
    };
})(document);

