;(function ($, _, undefined) {
    'use strict';
    ips.createModule('ips.storm.profiler', function () {
        // Functions that become public methods

        var respond = function (elem) {
            if (!$(elem).data('_stormProfiler')) {
                let profilerObject = new _stormProfiler($(elem));
                profilerObject.init();
                $(elem).data('_stormProfiler', profilerObject);
            }
        };

        // Register this module as a widget to enable the data API and
        // jQuery plugin functionality
        ips.ui.registerWidget('stormprofiler', ips.storm.profiler);

        // Expose public methods
        return {
            respond: respond,
        };
    });

    let _stormProfiler = function (el) {
        var ajax = ips.getAjax(),
            url = ips.getSetting("baseURL") + "?app=storm&module=profiler&controller=debug", zd = ips.ui.zIndex(),
            checker = null,
            init = function () {
                $('.stormProfilerPanelsContainer').css('zIndex', zd);
                zd = ips.ui.zIndex();
                let shim = $('#ElstormProfilerShim'),
                    reorder = $('#elReorderAppMenu'),
                    devWarning = $('.cAdminDevModeWarning'),
                    bar = $('#ElstormProfilerBar'),
                    bh = bar.outerHeight();
                shim.css('height', bh + 'px');
                if (reorder.length !== 0) {
                    reorder.css('bottom', bh + 52 + 'px');
                }
                if (devWarning.length !== 0) {
                    devWarning.css('bottom', bh + 5 + 'px');
                }
                bar.css('zIndex', zd);
                zd = ips.ui.zIndex();
                el.on('click', '[data-button]', _open);
                el.on('click', '[data-debug]', e => {
                    e.preventDefault();
                    let win = window.open(
                        $(e.currentTarget).attr('href'),
                        'Storm Debug Log',
                        'width=1000,height=600'
                    );
                    win.focus();
                });
                $(document).on('click', (e) => {
                    let target = $(e.target);
                    if(target.length !== 0) {
                        let parents1 = target.closest('.stormProfilerPanelsContainer'),
                            parents2 = target.closest('.stormProfilerBar'),
                            parents3 = target.parents('.ipsAlert');
                        if (parents1.length === 0 && parents2.length === 0 && parents3.length === 0) {
                            _close();
                        }
                    }
                });
                $(window).on('resize', (e) => {
                    shim.css('height', bar.outerHeight() + 'px');
                    if (reorder.length !== 0) {
                        reorder.css('bottom', bar.outerHeight() + 52 + 'px');
                    }
                    if (devWarning.length !== 0) {
                        devWarning.css('bottom', bar.outerHeight() + 5 + 'px');
                    }

                    let h = $('#ElstormProfilerBar').outerHeight();
                    $('#elStormProfilerPanelsContainer').css('bottom', h+'px');

                });
                $(document).on('stormProfilerClosePanel', () => {
                    _close();
                })
            },
            _close = function() {
                let panel = $(document).find('.stormProfilerPanelActive'),
                    pid;
                if(panel.length !== 0) {
                    pid = panel.attr('id');
                    panel.slideUp().removeClass('stormProfilerPanelActive').data('active', 0);
                }
                $('.stormProfilerButton')
                    .removeClass('stormProfilerDim')
                    .find('.fa-chevron-up')
                    .removeClass('fa-rotate-180');;
            },
            _open = function (e) {
                e.preventDefault();
                let target = $(e.currentTarget),
                    panel = $('#' + target.data('panel'));

                $('.stormProfilerButton').addClass('stormProfilerDim');
                if (panel.data('active') !== 1) {
                    let h = $('#ElstormProfilerBar').outerHeight();
                    $('#elStormProfilerPanelsContainer').css('bottom', h+'px');
                    $('[data-panels]').data('active', 0).hide().promise().done(function (e) {
                        $('.stormProfilerButton')
                            .removeClass('stormProfilerButtonActive')
                            .find('.fa-chevron-up')
                            .removeClass('fa-rotate-180');;
                        panel
                            .slideDown()
                            .css('zIndex', zd)
                            .data('active', 1)
                            .addClass('stormProfilerPanelActive');
                        target
                            .addClass('stormProfilerButtonActive')
                            .removeClass('stormProfilerFlash')
                            .removeClass('stormProfilerDim');
                        target
                            .find('.fa-chevron-up')
                            .addClass('fa-rotate-180');
                        $(document)
                            .trigger('stormProfilerPanelOpen', {panel: target.data('panel'), button: target});
                    });
                } else {
                    panel.data('active', 0).slideUp().promise().done(function(){
                        target
                            .removeClass('stormProfilerButtonActive')
                            .removeClass('stormProfilerFlash');
                        target
                            .find('.fa-chevron-up')
                            .removeClass('fa-rotate-180');
                        panel.removeClass('stormProfilerPanelActive');
                        $('.stormProfilerButton').removeClass('stormProfilerDim');
                    });

                    $(document).trigger('stormProfilerPanelOff', {panel: target.data('panel'), button: target})
                }
            }

        return {
            init: init
        }
    }
}(jQuery, _));

;(function ($, _, undefined) {
    'use strict';
    ips.createModule('ips.storm.profiler.proxy', function () {
        // Functions that become public methods

        var respond = function (elem, options) {
            if (!$(elem).data("_stormProfilerProxy")) {
                let stormProfilerProxy = new _stormProfilerProxy($(elem), options);
                stormProfilerProxy.init();
                $(elem).data("_stormProfilerProxy", stormProfilerProxy);
            }
        };

        // Register this module as a widget to enable the data API and
        // jQuery plugin functionality
        ips.ui.registerWidget("stormprofilerproxy", ips.storm.profiler.proxy);

        // Expose public methods
        return {
            respond: respond,
        };
    });

    let _stormProfilerProxy = function (el, options) {
        var ajax = ips.getAjax(),
            url = ips.getSetting("baseURL") +
                "?app=storm&module=other&controller=proxy",
            init = function () {
                _do('constants');
            },
            _do = generator => {
                ajax({
                    type: 'GET',
                    data: {do: generator},
                    url: url,
                    beforeSend: () => {
                        let msg = 'Building proxy: ' + generator,
                            altMsg = 'Starting Toolbox Meta: ',
                            altMsg1 = 'Starting Registrar & Providers: ',
                            steps = {
                                'constants': msg,
                                'settings': msg,
                                'request': msg,
                                'store': msg,
                                'models': msg,
                                'nonOwnedModels': msg,
                                'css': msg,
                                'phpCache': altMsg + ' PHP Data',
                                'phtmlCache': altMsg + 'PHTML Data',
                                'applications': altMsg1 + 'Applications',
                                'database': altMsg1 + 'Database',
                                'languages': altMsg1 + 'Language',
                                'extensions': altMsg1 + 'Extensions',
                                'templates': altMsg1 + 'Templates',
                                'moderators': altMsg1 + 'Moderators',
                                'url': altMsg1 + 'Query Strings & Furl\'s',
                                'errorCodes': altMsg1 + 'ErrorCodes',
                                'phpstormMeta': altMsg + 'PHPStorm Meta File',
                                'toolboxMeta': altMsg + 'Toolbox Meta',
                            };

                        _write(steps[generator], true)
                    },
                    success: (data) => {
                        _write(data.message, false);
                    },
                    complete: () => {
                        switch (generator) {
                            case 'constants':
                                _do('settings');
                                break;
                            case 'settings':
                                _do('request');
                                break;
                            case 'request':
                                _do('store');
                                break;
                            case 'store':
                                _do('css');
                                break;
                            case 'css':
                                _do('phpCache');
                                break;
                            case 'phpCache':
                                _do('phtmlCache');
                                break;
                            case 'phtmlCache':
                                _do('models');
                                break;
                            case 'models':
                                _do('nonOwnedModels');
                                break;
                            case 'nonOwnedModels':
                                _do('applications');
                                break;
                            case 'applications':
                                _do('database');
                                break;
                            case 'database':
                                _do('languages');
                                break;
                            case 'languages':
                                _do('extensions');
                                break;
                            case 'extensions':
                                _do('templates');
                                break;
                            case 'templates':
                                _do('moderators');
                                break;
                            case 'moderators':
                                _do('url');
                                break;
                            case 'url':
                                _do('errorCodes');
                                break;
                            case 'errorCodes':
                                _do('phpstormMeta');
                                break;
                            case 'phpstormMeta':
                                _do('toolboxMeta');
                                break;
                            case 'toolboxMeta':
                                break;
                        }
                    }
                });
            },
            _write = (message, first) => {
                let html = $('<div>'),
                    block = $('<div>'),
                    block2 = $('<div>'),
                    msg = $('<div>'),
                    id = 'block' + _randomString(5);


                if (first === true) {
                    block2.attr('data-block', 1).attr('id', id).addClass('stormProfilerLoader');
                    block.addClass('stormProfilerBlock').html(block2);
                } else {
                    $('[data-block]').removeClass('stormProfilerLoader').addClass('stormProfilerCheck');
                    block = '';
                }
                msg.addClass('stormProfilerBlock').html(message);
                html.addClass('ipsClearfix').prepend(msg).append(block);
                el.append(html);

            },
            _randomString = (length) => {
                if (_.isUndefined(length)) {
                    length = 10;
                }
                let characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789',
                    result = '',
                    charactersLength = characters.length;
                for (let i = 0; i < length; i++) {
                    result += characters.charAt(Math.floor(Math.random() * charactersLength));
                }
                return result;
            };
        return {
            init: init
        }
    }
}(jQuery, _));

;(function ($, _, undefined) {
    'use strict';
    ips.createModule('ips.storm.profiler.search', function () {
        // Functions that become public methods

        var respond = function (elem) {
            if(!$(elem).data('_stormProfilerSearch')){
                    let profilerSearchObject = new _stormProfilerSearch($(elem));
                    profilerSearchObject.init();
                    $(elem).data('_stormProfilerSearch', profilerSearchObject);
                } 
            };

        // Register this module as a widget to enable the data API and
        // jQuery plugin functionality
        ips.ui.registerWidget('stormprofilersearch', ips.storm.profiler.search);

        // Expose public methods
        return {
            respond: respond,
        };
    });

    let _stormProfilerSearch = function (el) { 
        var init = function () {
            let buttonId = el.attr('id').replace('_panel','');
            el.on('click', '[data-wipe]', e => {
                el.find('.stormProfilerPanelChild').empty();
                $('#'+buttonId).find('.stormProfilerCount').html('0');
            });
            el.on('click', '[data-clear]', (e) => {
                e.preventDefault();
                let t = $(e.currentTarget);
                t.prev().val('');
                el.find('.stormProfilerPanelChild').find('.removeLater').remove();
                $.each(el.find('[data-searchable]'), (i, v) => {
                    $(v).css('opacity', 1);
                });
                t.next().html('');
            });
            el.on('keyup', '[data-search]', (e) => {
                e.preventDefault();
                let target = $(e.currentTarget),
                    val = target.val(),
                    ii = 0;
                el.find('.stormProfilerPanelChild').find('.removeLater').remove();
                $.each(el.find('[data-searchable]'), (i, v) => {
                    $(v).css('opacity', 1);
                });
                $.each(el.find('[data-searchable]'), (i, v) => {
                    let t = $(v),
                        href = t.attr('data-src');
                    if (href.indexOf(val) === -1) {
                        t.css('opacity', 0.2);
                    }
                    else{
                        t.css('opacity', 0.2);
                        let f = t.clone();
                        el.find('.stormProfilerPanelChild').prepend(f.addClass('removeLater').css('opacity',1));
                        //t.addClass('ipsHide');
                        ii++;
                    }
                });
                if(ii !== 0){
                    target.next().next().html('('+ii+') results found!');
                }
            });
            el.on('click', '[data-close]', (e) => {
                e.preventDefault();
                $(document).trigger('stormProfilerClosePanel');
            });
        } 

        return {
            init: init
        }
    }
}(jQuery, _));

;( function($, _, undefined){
    "use strict";
    ips.createModule('ips.ui.storm.uuid', () => {
        /**
         * Respond to a dialog trigger
         *
         * @param   {element}   elem        The element this widget is being created on
         * @param   {object}    options     The options passed
         * @param   {event}     e           if lazyload, event that is fire
         * @returns {void}
         */
         const respond = (elem, options, e) => {
            let el = $(elem);
            if (!el.data('_loadedDevToysuuid')) {
                let mobject = _objectDevToysuuid(el, options);
                mobject.init();
                el.data('_loadedDevToysuuid', mobject);
            }
        },
        /**
         * Retrieve the instance (if any) on the given element
         *
         * @param	{element} 	elem 		The element to check
         * @returns {mixed} 	The instance or undefined
         */
        getObj = (elem) => {
            if( $( elem ).data('_loadedDevToysuuid') ){
                return $( elem ).data('_loadedDevToysuuid');
            }
            return undefined;
        };

        // Register this module as a widget to enable the data API and
        // jQuery plugin functionality
        ips.ui.registerWidget( 'stormdevtoysuuid', ips.ui.storm.uuid, [] );

        // Expose public methods
        return {
            respond: respond,
            getObj: getObj
        };
    });
    const _objectDevToysuuid = function(elem, options) {
        var ajax = ips.getAjax(),
            init = () => {
                elem.on('click','#clearuuid',_clear);
                elem.on('click','#generate',_generate);
            },
            _generate = e => {
                let form = elem.find('form:first'),
                    values = form.serialize(),
                    action = ips.getSetting('baseURL')+'?app=storm&module=other&controller=toys&do=uuid';
                ajax({
                    type: "POST",
                    url: action,
                    data:values,
                    bypassRedirect: true,
                    success: function (data) {
                        $('#elUuidContainer').append($(data));
                    }
                });
            },
        _clear = e => {
            let target = $(e.currentTarget);
            $('#elUuidContainer').html('');
        };
        return {
            init: init
        }
    };
}(jQuery, _));
